(function(){const n=document.querySelector(".carousel-track");if(!n)return;const t=Array.from(n.children),o=document.querySelector(".carousel-button.next"),i=document.querySelector(".carousel-button.prev");let e=0;function s(){const s=t[0].getBoundingClientRect().width;n.style.transform=`translateX(-${e*s}px)`}o&&o.addEventListener("click",()=>{e<t.length-1&&(e++,s())}),i&&i.addEventListener("click",()=>{e>0&&(e--,s())}),window.addEventListener("DOMContentLoaded",()=>{const e=t[0].getBoundingClientRect().width;t.forEach((t,n)=>{t.style.left=`${n*e}px`})}),window.addEventListener("resize",s)})()